import React, { useState } from 'react';

const ScraperForm = () => {
  const [url, setUrl] = useState('');
  const [data, setData] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:8080/scrape?url=${encodeURIComponent(url)}`);
      if (response.ok) {
        const result = await response.json();
        setData(result);
      } else {
        console.error('Response not ok');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="p-4">
      <form onSubmit={handleSubmit} className="flex flex-col space-y-2">
        <input
          type="text"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          placeholder="Enter eBay item URL"
          className="px-3 py-2 border border-gray-300 rounded"
        />
        <button type="submit" className="px-3 py-2 bg-blue-500 text-white rounded">
          Scrape
        </button>
      </form>
      {data && <div className="mt-4">
        <div>Title: {data.title}</div>
        <div>Images: {data.imageUrls.join(', ')}</div>
      </div>}
    </div>
  );
};

export default ScraperForm;
