import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ScraperForm = () => {
    const [url, setUrl] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            const response = await fetch(`http://localhost:8080/scrape?url=${encodeURIComponent(url)}`);
            setIsLoading(false);
            if (response.ok) {
                const blob = await response.blob();
                const downloadUrl = window.URL.createObjectURL(blob);
                const link = document.createElement('a');
                link.href = downloadUrl;
                link.download = "data.zip";
                document.body.appendChild(link);
                link.click();
                link.remove();
                window.URL.revokeObjectURL(downloadUrl);
                toast.success('Data fetched and download started!');
            } else {
                toast.error('Failed to fetch data.');
            }
        } catch (error) {
            setIsLoading(false);
            toast.error('An error occurred!');
            console.error('Error:', error);
        }
    };

    return (
        <div className="p-4">
            <form onSubmit={handleSubmit} className="flex flex-col space-y-2">
                <input
                    type="text"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    placeholder="Enter eBay item URL"
                    className="px-3 py-2 border border-gray-300 rounded"
                    disabled={isLoading}
                />
                <button type="submit" className="px-3 py-2 bg-blue-500 text-white rounded" disabled={isLoading}>
                    {isLoading ? "Loading..." : "Scrape"}
                </button>
            </form>
            <ToastContainer position="bottom-center" />
        </div>
    );
};

export default ScraperForm;
